import * as React from "react";
import "../src/index.css";
import { get, set, values, del, update } from "idb-keyval";
import { useEffect, useState } from "react";
import { v4 as uuidv4 } from "uuid";
import App from "./homepage";

function MainApp() {
  const [inputlist, setInputList] = useState("");
  const [b, setb] = useState(true);
  const [Items, setItems] = useState([]);

  useEffect(() => {
    setb(true);
    get("TODO").then((val) => {
      if (val) setItems(val);
    });
  }, []);

  const clickHandler = () => {
    if (inputlist !== "") {
      set("TODO", [...Items, { name: inputlist, id: uuidv4() }])
        .then(() => {
          get("TODO").then((val) => {
            console.log("worked");
            setInputList("");
          });
        })
        .catch((err) => console.log("It failed!", err));

      get("TODO").then((val) =>
        setItems(() => {
          console.log(val);
          return val;
        })
      );
    }
  };

  const deleteHandler = (name) => {
    // console.log(Items.length);
    setItems((Items) => Items.filter((Items) => Items.id !== name.id));
  };

  const updateHandler = (attr) => {
    // console.log(attr.name);
    setb(false);

    setInputList(attr.name);
  };

  const itemEvent = (event) => {
    setInputList(event.target.value);
  };

  return (
    <div classNameName="container">
      <div classNameName="App">
        <br />
        {/* <div className="d-grid gap-2 d-md-flex justify-content-md-end">
          <button className="btn btn-primary me-md-2" type="button">
            Button
          </button>
        </div> */}

        <br />
        <br />
        <br />
        <div classNameName="container text-center col-md-6 col-md-offset-6">
          <div classNameName="card h-100 row align-items-center">
            <div classNameName="card-header">TO DO TASK</div>
            <div classNameName="card-body">
              <form action="submit">
                <div classNameName="mb-3">
                  <textarea
                    classNameName="form-control"
                    id="exampleFormControlTextarea1"
                    rows="3"
                    placeholder="Add Your Work... "
                    onChange={itemEvent}
                    value={inputlist}
                  ></textarea>
                  <br />
                  {b ? (
                    <button
                      type="button"
                      classNameName="btn btn-dark"
                      onClick={() => clickHandler()}
                    >
                      Add
                    </button>
                  ) : (
                    <button
                      type="button"
                      ng-hide="false"
                      classNameName="btn btn-dark"
                      onClick={() => clickHandler()}
                    >
                      Update
                    </button>
                  )}
                </div>
              </form>
            </div>
            <div classNameName="list">
              <ol className="list-group list-group-numbered">
                {Items.map((itemval) => (
                  <li className="list-group-item">
                    {itemval.name} &nbsp; &nbsp; &nbsp;{" "}
                    <button type="button" className="btn">
                      <i
                        className="fa-solid fa-pencil"
                        onClick={() => updateHandler(itemval)}
                      ></i>
                    </button>
                    &nbsp;&nbsp;&nbsp;
                    <button type="button" className="btn">
                      <i
                        className="fa-solid fa-trash"
                        onClick={() => deleteHandler(itemval)}
                      ></i>
                    </button>
                  </li>
                ))}
              </ol>
              <br />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default MainApp;
