import * as React from "react";
// import "./App.css";
import { get, set, values, del } from "idb-keyval";
import { useEffect, useState } from "react";
import { v4 as uuidv4 } from "uuid";
import App from "./homepage";

function MainApp() {
  // values().then((values) => (a = values));
  // console.log(a);
  const [inputlist, setInputList] = useState("");
  const [Items, setItems] = useState([]);

  useEffect(() => {
    get("TODO").then((val) => {
      if (val) setItems(val);
    });
  }, []);

  const clickHandler = () => {
    set("TODO", [...Items, { name: inputlist, id: uuidv4() }])
      .then(() => {
        get("TODO").then((val) => {
          console.log("worked");
          setInputList[""];
        });
      })
      .catch((err) => console.log("It failed!", err));

    // console.log(count);

    // values().then((values) =>
    //   setItems(() => {
    //     console.log(values);
    //     return [values];
    //   })
    // );

    get("TODO").then((val) =>
      setItems(() => {
        console.log(val);
        return val;
      })
    );
  };

  const deleteHandler = (name) => {
    console.log(Items.length);
    for (let i = 0; i < Items.length; i++) {
      // console.log(Items[i].id);
      // console.log(id.id);
      if (name.id == Items[i].id) {
        // Items[0] = Items[1];
        console.log(Items[i + 1]);
        console.log("matched");
      }
    }

    // del("TODO");
  };

  const itemEvent = (event) => {
    setInputList(event.target.value);
    // console.log({ inputlist });
  };

  return (
    <div className="container">
      <div className="App">
        <br />
        <br />
        <br />
        <br />
        <br />
        <br />
        <div className="container text-center col-md-6 col-md-offset-6">
          <div className="card h-100 row align-items-center">
            <div className="card-header">TO DO TASK</div>
            <div className="card-body">
              <div className="mb-3">
                <textarea
                  className="form-control"
                  id="exampleFormControlTextarea1"
                  rows="3"
                  placeholder="Add Your Work... "
                  onChange={itemEvent}
                  value={inputlist}
                ></textarea>
                <br />
                <button
                  type="button"
                  className="btn btn-dark"
                  onClick={() => clickHandler()}
                >
                  Add
                </button>
              </div>
            </div>
            <div>
              <ol class="list-group list-group-numbered">
                {Items.map((itemval) => {
                  return (
                    <li class="list-group-item">
                      {itemval.name} &nbsp; &nbsp; &nbsp;{" "}
                      <button type="button" class="btn">
                        <i
                          class="fa-solid fa-trash"
                          onClick={() => deleteHandler(itemval)}
                        ></i>
                      </button>
                    </li>
                  );
                })}
              </ol>
              <br />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default MainApp;
